import React, { useState } from "react"
import { makeStyles } from "@material-ui/core/styles"
import { Button, Drawer, List, ListItem, ListItemText } from "@material-ui/core"
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom"

const styles = makeStyles({})

export default function AppDrawer() {
  const classes = styles()
  const [drawerState, setDrawerState] = useState(false)

  const toggleDrawer = (open) => {
    if (drawerState == false) {
      setDrawerState(open)
    }
  }

  return (
    <div>
      <Button onClick={() => toggleDrawer(true)}>Open from left</Button>

      <Drawer variant="persistent" anchor={"left"} open={drawerState} onClose={() => toggleDrawer(false)}>
        <List>
          <ListItem onClick={toggleDrawer(false)}>
            <ListItemText>Home</ListItemText>
          </ListItem>
        </List>
      </Drawer>
    </div>
  )
}
