import React, { useState } from "react"
import { makeStyles } from "@material-ui/core/styles"
import { Button, Drawer, List, ListItem, ListItemText } from "@material-ui/core"
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom"

const styles = makeStyles({
  drawerPaper: {
    width: "inherit",
  },
})

export default function AppDrawer() {
  const classes = styles()
  const [drawerState, setDrawerState] = useState(false)

  const toggleDrawer = (open) => {
    if (drawerState == false) {
      setDrawerState(true)
    } else {
      setDrawerState(false)
    }
  }

  return (
    <Router>
      <Button onClick={() => toggleDrawer(true)}>Open from left</Button>
      <div style={{ display: "flex" }} classes={{ paper: paperclasses.drawerPaper }}>
        <Drawer style={{ width: "220px" }} variant="persistent" anchor={"left"} open={drawerState}>
          <List>
            <ListItem button onClick={() => toggleDrawer(false)}>
              <ListItemText>Home</ListItemText>
            </ListItem>
          </List>
        </Drawer>
      </div>
    </Router>
  )
}
